sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    //Só executar se houve realmente mudança no valor do campo
    if (isLoading || newValue === '' || newValue === oldValue) {
        return;
    }
    //Instanciar um GlideAjax do script criado
    var getEnderecoEmail = new GlideAjax("getEnderecoEmail");
    //Adicionar o parametro do nome do método do objeto sendo chamado, no caso getEmail
    getEnderecoEmail.addParam("sysparm_name", "getEmail");
    //getEnderecoEmail.addParam("sysparm_name","getEmailDummy");

    //Pega o valor do campo a ser usado como parâmetro da consulta ao serverside
    var userId = g_form.getValue("u_requested_for");
    //Atribui o parâmetro do Id usado na busca ao banco
    getEnderecoEmail.addParam("sysparm_userID", userId);
    //Faz a chamada assíncrona, passar a callBack como parâmetro, no caso thenDo(resposta com o XML contendo e-mail)
    getEnderecoEmail.getXML(thenDo);

    function thenDo(response) {
        console.log(response);
        //Pegar só o campo que interessa da resposta XML
        var emailBuscado = response.responseXML.documentElement.getAttribute("answer");
		//Limpa o email que estiver no campo e atribui o buscado do backend.
        g_form.clearValue('u_requested_for_email');
		//Atribuir ao campo email o valor buscado pelo comando da linha 22
        g_form.setValue('u_requested_for_email', emailBuscado);
    }

}
