sys_script_include.script:
var getEnderecoEmail = Class.create();
getEnderecoEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//Na chamada usar addParam("sysparm_name",<nome do método> no caso getEmail)
	getEmail: function(){
		var usuario = new GlideRecord("sys_user");
		usuario.get(this.getParameter("sysparm_userID"));
		return usuario.email+'';
	},

	getEmailDummy: function(){
		return "email@teste123.com";
	},

    type: 'getEnderecoEmail'
});
