sys_ui_action.script:
(function() {

	try {
		var inputs = {};
		inputs['tarefa_needit'] = current; // GlideRecord of table: x_58872_needit_needit_task 
		inputs['motivo'] = "Tarefa atrasada fechada manualmente"; // String 

		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		// sn_fd.FlowAPI.getRunner().subflow('x_794790_needit_sp.tratar_as_taferas_atrasadas_do_needit').inBackground().withInputs(inputs).run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		var result = sn_fd.FlowAPI.getRunner().subflow('x_794790_needit_sp.tratar_as_taferas_atrasadas_do_needit').inForeground().withInputs(inputs).run();
		var outputs = result.getOutputs();

		// Get Outputs:
		// Note: outputs can only be retrieved when executing synchronously.
		var tarefa_followup = outputs['tarefa_followup']; // Reference
		//Navegar para a nova tarefa followup criada.
		action.setRedirectURL(tarefa_followup);
		//Na tarefa followup o botão retornar volta para a tarefa sendo fechada
		action.setReturnURL(current);
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}

})();
